import React, { useState } from 'react';
import { Container, Typography, List, ListItem, ListItemText, ListItemSecondaryAction, IconButton, Button, Dialog, DialogTitle, DialogContent } from '@mui/material';
import { Delete } from '@mui/icons-material';
import QRCode from 'react-qr-code';

const Cart = () => {
  const [cartItems, setCartItems] = useState([
    { id: 1, name: 'Burger', price: 5.99, quantity: 2 },
    { id: 2, name: 'Pizza', price: 8.99, quantity: 1 },
  ]);
  const [showQR, setShowQR] = useState(false);
  const [orderId, setOrderId] = useState(null);

  const removeFromCart = (itemId) => {
    setCartItems(cartItems.filter(item => item.id !== itemId));
  };

  const getTotal = () => {
    return cartItems.reduce((total, item) => total + (item.price * item.quantity), 0).toFixed(2);
  };

  const handleCheckout = () => {
    // Generate a unique order ID
    const newOrderId = 'ORD' + Date.now();
    setOrderId(newOrderId);
    setShowQR(true);

    // Save order to local storage or backend
    const order = {
      id: newOrderId,
      items: cartItems,
      total: getTotal(),
      status: 'pending',
      timestamp: new Date().toISOString()
    };

    // Clear cart after order
    setCartItems([]);
  };

  return (
    <Container>
      <Typography variant="h4" style={{ margin: '20px 0' }}>
        Shopping Cart
      </Typography>

      <List>
        {cartItems.map((item) => (
          <ListItem key={item.id}>
            <ListItemText
              primary={item.name}
              secondary={`Quantity: ${item.quantity} | $${(item.price * item.quantity).toFixed(2)}`}
            />
            <ListItemSecondaryAction>
              <IconButton edge="end" onClick={() => removeFromCart(item.id)}>
                <Delete />
              </IconButton>
            </ListItemSecondaryAction>
          </ListItem>
        ))}
      </List>

      <Typography variant="h6" style={{ margin: '20px 0' }}>
        Total: ${getTotal()}
      </Typography>

      <Button
        variant="contained"
        color="primary"
        onClick={handleCheckout}
        disabled={cartItems.length === 0}
      >
        Checkout
      </Button>

      <Dialog open={showQR} onClose={() => setShowQR(false)}>
        <DialogTitle>Order QR Code</DialogTitle>
        <DialogContent style={{ padding: '20px', textAlign: 'center' }}>
          {orderId && (
            <>
              <QRCode value={orderId} />
              <Typography style={{ marginTop: '20px' }}>
                Order ID: {orderId}
              </Typography>
              <Typography style={{ marginTop: '10px' }}>
                Show this QR code to the seller to collect your order
              </Typography>
            </>
          )}
        </DialogContent>
      </Dialog>
    </Container>
  );
};

export default Cart;
