import React, { useState, useRef, useEffect } from 'react';
import { Container, Typography, Paper, Button, Box } from '@mui/material';
import axios from 'axios';
import { API_URL } from '../../config';

const QRScanner = () => {
  const [scannedOrders, setScannedOrders] = useState({});
  const [result, setResult] = useState(null);
  const [loading, setLoading] = useState(false);
  const videoRef = useRef();
  const canvasRef = useRef();
  const [scanning, setScanning] = useState(false);

  useEffect(() => {
    return () => {
      if (scanning) {
        stopScanning();
      }
    };
  }, [scanning]);

  const startScanning = async () => {
    try {
      setScanning(true);
      const stream = await navigator.mediaDevices.getUserMedia({ 
        video: { facingMode: "environment" } 
      });

      videoRef.current.srcObject = stream;
      videoRef.current.play();

      // Start processing frames
      requestAnimationFrame(processFrame);
    } catch (error) {
      console.error('Error accessing camera:', error);
      setResult({
        status: 'error',
        message: 'Unable to access camera. Please make sure you have granted camera permissions.'
      });
      setScanning(false);
    }
  };

  const stopScanning = () => {
    if (videoRef.current?.srcObject) {
      const tracks = videoRef.current.srcObject.getTracks();
      tracks.forEach(track => track.stop());
      videoRef.current.srcObject = null;
    }
    setScanning(false);
  };

  const processFrame = async () => {
    if (!scanning || !videoRef.current || !canvasRef.current) return;

    const video = videoRef.current;
    const canvas = canvasRef.current;
    const context = canvas.getContext('2d');

    // Make sure video is ready
    if (video.readyState === video.HAVE_ENOUGH_DATA) {
      // Set canvas size to match video
      canvas.width = video.videoWidth;
      canvas.height = video.videoHeight;

      // Draw current frame to canvas
      context.drawImage(video, 0, 0, canvas.width, canvas.height);

      try {
        // Get image data from canvas
        const imageData = context.getImageData(0, 0, canvas.width, canvas.height);

        // Here you would normally process the image data to find QR codes
        // For testing purposes, let's simulate finding a QR code after 3 seconds
        await new Promise(resolve => setTimeout(resolve, 3000));

        // Simulate finding a QR code (replace this with actual QR code detection)
        const orderId = "sample_order_id"; // This should come from actual QR code

        // Stop scanning and process the order
        stopScanning();
        await handleOrderId(orderId);
      } catch (error) {
        console.error('Error processing frame:', error);
      }
    }

    // Continue scanning if still active
    if (scanning) {
      requestAnimationFrame(processFrame);
    }
  };

  const handleOrderId = async (orderId) => {
    try {
      setLoading(true);

      // Check if order was already scanned
      if (scannedOrders[orderId]) {
        setResult({
          orderId,
          status: 'already_served',
          message: 'This order has already been served!'
        });
        return;
      }

      // Fetch order details from backend
      const response = await axios.get(`${API_URL}/orders/${orderId}`);
      const order = response.data;

      // Mark order as served using PATCH request
      await axios.patch(`${API_URL}/orders/${orderId}`, { status: 'completed' });
      setScannedOrders({ ...scannedOrders, [orderId]: true });

      setResult({
        orderId,
        status: 'new_order',
        message: 'Order found and marked as completed!',
        details: {
          orderId: order._id,
          items: order.items.map(item => ({
            name: item.productId?.name || 'Unknown Product',
            quantity: item.quantity,
            price: item.price
          })),
          timestamp: new Date(order.createdAt).toLocaleString(),
          total: order.total
        }
      });
    } catch (error) {
      console.error('Error processing order:', error);
      let errorMessage = 'Failed to process order';

      if (error.response?.status === 404) {
        errorMessage = 'Invalid QR code. Please try scanning again.';
      } else if (error.response?.data?.message) {
        errorMessage = error.response.data.message;
      }

      setResult({
        status: 'error',
        message: errorMessage
      });
    } finally {
      setLoading(false);
    }
  };

  return (
    <Container>
      <Typography variant="h4" style={{ margin: '20px 0' }}>
        QR Code Scanner
      </Typography>

      <Paper style={{ padding: '20px', marginBottom: '20px' }}>
        <Box textAlign="center">
          <div style={{ position: 'relative', maxWidth: '100%', margin: '0 auto' }}>
            <video
              ref={videoRef}
              style={{
                width: '100%',
                maxWidth: '500px',
                display: scanning ? 'block' : 'none'
              }}
            />
            <canvas
              ref={canvasRef}
              style={{ display: 'none' }}
            />
          </div>

          <Button
            variant="contained"
            color={scanning ? "secondary" : "primary"}
            onClick={() => scanning ? stopScanning() : startScanning()}
            style={{ margin: '20px 0' }}
            disabled={loading}
          >
            {loading ? 'Processing...' : scanning ? 'Stop Scanning' : 'Start Scanning'}
          </Button>
        </Box>
      </Paper>

      {result && (
        <Paper style={{ 
          padding: '20px', 
          backgroundColor: 
            result.status === 'already_served' ? '#ffebee' : 
            result.status === 'error' ? '#fff3e0' : '#e8f5e9' 
        }}>
          <Typography variant="h6" gutterBottom>
            {result.message}
          </Typography>

          {result.details && (
            <>
              <Typography variant="subtitle1" gutterBottom>
                Order ID: {result.details.orderId}
              </Typography>
              <Typography variant="subtitle1" gutterBottom>
                Time: {result.details.timestamp}
              </Typography>
              <Typography variant="subtitle1">Items:</Typography>
              {result.details.items.map((item, index) => (
                <Typography key={index}>
                  - {item.name} x {item.quantity} - ${(item.price * item.quantity).toFixed(2)}
                </Typography>
              ))}
              <Typography variant="h6" style={{ marginTop: '10px' }}>
                Total: ${result.details.total.toFixed(2)}
              </Typography>
            </>
          )}
        </Paper>
      )}

      {result && (
        <Button
          variant="contained"
          color="primary"
          onClick={() => {
            setResult(null);
            if (!scanning) {
              startScanning();
            }
          }}
          style={{ marginTop: '20px' }}
        >
          Scan Next Order
        </Button>
      )}
    </Container>
  );
};

export default QRScanner;
